package server_test

import (
	"net/http"
	"net/http/httptest"
	"testing"

	"github.com/gorilla/mux"
	"github.com/stretchr/testify/require"

	"github.com/your_package/server"
)

func TestHelloWorldHandler(t *testing.T) {
	handler := http.HandlerFunc(server.HelloWorldHandler)

	router := mux.NewRouter()
	router.Handle("/", handler)

	tests := []struct {
		description string
		method      string
		status      int
		response    string
	}{
		{
			description: "Check HelloWorldHandler responds with a successful HTTP Status Code of 200",
			method:      http.MethodGet,
			status:      http.StatusOK,
			response:    "Hello World!",
		},
	}

	for _, test := range tests {
		t.Run(test.description, func(t *testing.T) {
			req, err := http.NewRequest(test.method, "/", nil)
			require.NoError(t, err)

			rr := httptest.NewRecorder()
			router.ServeHTTP(rr, req)

			require.Equal(t, test.status, rr.Code)
			require.Equal(t, test.response, rr.Body.String())
		})
	}
}
